const SPRITE_DATA = {"enemy": {"width": 48, "height": 60, "data": ""}, "enemy_dead": {"width": 49, "height": 63, "data": ""}, "pistol": {"width": 82, "height": 92, "data": ""}, "pistol_fire": {"width": 82, "height": 96, "data": ""}, "shotgun": {"width": 67, "height": 62, "data": ""}, "shotgun_fire": {"width": 88, "height": 128, "data": ""}, "chaingun": {"width": 106, "height": 51, "data": ""}, "chaingun_fire": {"width": 106, "height": 47, "data": ""}, "rocket": {"width": 59, "height": 20, "data": ""}, "plasma": {"width": 126, "height": 76, "data": ""}, "plasma_fire": {"width": 126, "height": 68, "data": ""}, "bfg": {"width": 130, "height": 74, "data": ""}, "bfg_fire": {"width": 130, "height": 74, "data": ""}, "key_blue": {"width": 14, "height": 21, "data": ""}, "key_red": {"width": 14, "height": 21, "data": ""}, "key_yellow": {"width": 14, "height": 21, "data": ""}, "medikit": {"width": 29, "height": 20, "data": ""}, "armor_bonus": {"width": 13, "height": 19, "data": ""}, "ammo_clip": {"width": 7, "height": 12, "data": ""}, "ammo_shell": {"width": 14, "height": 10, "data": ""}, "door_red": {"width": 14, "height": 21, "data": ""}, "door_blue": {"width": 14, "height": 21, "data": ""}, "door_yellow": {"width": 14, "height": 21, "data": ""}};

const SPRITES = {};

function loadSprites() {
    Object.keys(SPRITE_DATA).forEach(key => {
        SPRITES[key] = new Image();
        SPRITES[key].src = 'data:image/png;base64,' + SPRITE_DATA[key].data;
        SPRITES[key].width = SPRITE_DATA[key].width;
        SPRITES[key].height = SPRITE_DATA[key].height;
    });
    console.log('Loaded', Object.keys(SPRITES).length, 'sprites');
}

// ============================================================================
// GAME CLASSES
// ============================================================================

class Weapon {
    constructor(name, damage, ammo, maxAmmo, fireRate, spread, pellets = 1, spriteName, fireSpriteName, infinite = false) {
        this.name = name;
        this.damage = damage;
        this.ammo = ammo;
        this.maxAmmo = maxAmmo;
        this.fireRate = fireRate;
        this.spread = spread;
        this.pellets = pellets;
        this.cooldown = 0;
        this.spriteName = spriteName;
        this.fireSpriteName = fireSpriteName;
        this.infinite = infinite;
    }
    
    canFire() {
        return this.cooldown === 0 && (this.infinite || this.ammo > 0);
    }
    
    fire() {
        if (!this.canFire()) return false;
        if (!this.infinite) {
            this.ammo--;
        }
        this.cooldown = this.fireRate;
        return true;
    }
    
    update() {
        if (this.cooldown > 0) this.cooldown--;
    }
    
    addAmmo(amount) {
        this.ammo = Math.min(this.ammo + amount, this.maxAmmo);
    }
}

class Enemy {
    constructor(x, y, type = 'demon') {
        this.x = x;
        this.y = y;
        this.type = type;
        this.health = 60;
        this.maxHealth = 60;
        this.speed = 0.015;
        this.damage = 5;
        this.attackCooldown = 0;
        this.attackRange = 1.5;
        this.detectionRange = 8;
        this.active = false;
        this.dead = false;
        this.size = 0.3;
    }
    
    update(playerX, playerY, canMoveCallback) {
        if (this.dead) return false;
        
        const dx = playerX - this.x;
        const dy = playerY - this.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        
        if (distance < this.detectionRange) {
            this.active = true;
        }
        
        if (this.active) {
            if (distance > this.attackRange) {
                // Calculate next position
                const moveX = (dx / distance) * this.speed;
                const moveY = (dy / distance) * this.speed;
                const nextX = this.x + moveX;
                const nextY = this.y + moveY;
                
                // Check if can move (wall collision)
                if (canMoveCallback && canMoveCallback(nextX, nextY)) {
                    this.x = nextX;
                    this.y = nextY;
                } else if (canMoveCallback) {
                    // Try moving only in X
                    if (canMoveCallback(nextX, this.y)) {
                        this.x = nextX;
                    }
                    // Try moving only in Y
                    else if (canMoveCallback(this.x, nextY)) {
                        this.y = nextY;
                    }
                }
            }
            
            if (this.attackCooldown > 0) {
                this.attackCooldown--;
            }
        }
        
        return distance <= this.attackRange && this.attackCooldown === 0;
    }
    
    attack() {
        this.attackCooldown = 60;
        return this.damage;
    }
    
    takeDamage(amount) {
        this.health -= amount;
        if (this.health <= 0) {
            this.dead = true;
            return true;
        }
        return false;
    }
    
    getAngle(playerX, playerY) {
        return Math.atan2(this.y - playerY, this.x - playerX);
    }
}

class Pickup {
    constructor(x, y, type) {
        this.x = x;
        this.y = y;
        this.type = type;
        this.collected = false;
        this.bobOffset = Math.random() * Math.PI * 2;
    }
    
    update(time) {
        // Bobbing animation
        this.bob = Math.sin(time * 0.003 + this.bobOffset) * 0.1;
    }
    
    checkCollision(playerX, playerY) {
        const dx = this.x - playerX;
        const dy = this.y - playerY;
        const distance = Math.sqrt(dx * dx + dy * dy);
        return distance < 0.5;
    }
    
    collect(player) {
        if (this.collected) return false;
        
        this.collected = true;
        
        switch(this.type) {
            case 'health':
                player.health = Math.min(player.health + 25, player.maxHealth);
                return true;
            case 'armor':
                player.armor = Math.min(player.armor + 25, 100);
                return true;
            case 'ammo':
                Object.values(player.weapons).forEach(w => {
                    if (!w.infinite) {
                        w.addAmmo(50);
                    }
                });
                return true;
            default:
                return false;
        }
    }
}

class Key {
    constructor(x, y, color) {
        this.x = x;
        this.y = y;
        this.color = color;
        this.collected = false;
        this.bobOffset = Math.random() * Math.PI * 2;
    }
    
    update(time) {
        this.bob = Math.sin(time * 0.003 + this.bobOffset) * 0.1;
        this.rotation = (time * 0.001) % (Math.PI * 2);
    }
    
    checkCollision(playerX, playerY) {
        const dx = this.x - playerX;
        const dy = this.y - playerY;
        const distance = Math.sqrt(dx * dx + dy * dy);
        return distance < 0.5;
    }
}

// ============================================================================
// MAIN GAME CLASS
// ============================================================================

class CrimsonDoom {
    constructor() {
        this.canvas = document.getElementById('gameCanvas');
        this.engine = new RaycastEngine(this.canvas);
        this.levelGen = new LevelGenerator(48, 48);
        
        this.running = false;
        this.level = 1;
        this.health = 100;
        this.maxHealth = 100;
        this.armor = 0;
        this.kills = 0;
        this.totalKills = 0;
        
        // Initialize weapons
        this.weapons = {
            pistol: new Weapon('PISTOL', 20, 999, 999, 12, 0.02, 1, 'pistol', 'pistol_fire', true),
            shotgun: new Weapon('SHOTGUN', 10, 24, 100, 30, 0.08, 7, 'shotgun', 'shotgun_fire'),
            chaingun: new Weapon('CHAINGUN', 15, 200, 400, 5, 0.03, 1, 'chaingun', 'chaingun_fire'),
            rocket: new Weapon('ROCKET', 100, 20, 50, 40, 0, 1, 'rocket', 'rocket'),
            plasma: new Weapon('PLASMA', 30, 200, 300, 8, 0.01, 1, 'plasma', 'plasma_fire'),
            bfg: new Weapon('BFG', 200, 40, 100, 60, 0.1, 20, 'bfg', 'bfg_fire')
        };
        
        this.weaponOrder = ['pistol', 'shotgun', 'chaingun', 'rocket', 'plasma', 'bfg'];
        this.currentWeaponIndex = 0;
        this.currentWeapon = this.weapons.pistol;
        
        this.unlockedWeapons = new Set(['pistol']);
        
        this.keys = {
            red: false,
            blue: false,
            yellow: false
        };
        
        this.enemies = [];
        this.pickups = [];
        this.keyItems = [];
        
        this.muzzleFlash = 0;
        this.screenShake = 0;
        
        // Pickup notification
        this.pickupMessage = '';
        this.pickupTimer = 0;
        this.damageFlash = 0;
        
        this.time = 0;
        
        this.ui = {
            health: document.querySelector('#health .stat-value'),
            armor: document.querySelector('#armor .stat-value'),
            ammo: document.querySelector('#ammo .stat-value'),
            weapon: document.querySelector('#weapon .stat-value'),
            kills: document.querySelector('#kills .stat-value'),
            level: document.querySelector('#level .stat-value'),
            keys: document.querySelector('#keys .stat-value'),
            container: document.getElementById('ui'),
            menu: document.getElementById('mainMenu'),
            gameOver: document.getElementById('gameOver'),
            levelComplete: document.getElementById('levelComplete'),
            finalKills: document.getElementById('finalKills'),
            finalLevel: document.getElementById('finalLevel'),
            weaponIndicator: document.getElementById('weaponIndicator'),
            currentWeaponName: document.getElementById('currentWeaponName')
        };
        
        // Volume control - initialize before setupControls to avoid NaN display
        this.masterVolume = parseFloat(localStorage.getItem('crimsonDoomVolume') || '1.0');
        
        loadSprites();
        this.setupControls();
        this.setupAudio();
        this.loadGame();
    }
    
    setVolume(volume) {
        this.masterVolume = Math.max(0, Math.min(1, volume));
        localStorage.setItem('crimsonDoomVolume', this.masterVolume.toString());
        
        // Update music volume if playing
        if (this.musicGain) {
            this.musicGain.gain.setValueAtTime(0.08 * this.masterVolume, this.audioContext.currentTime);
        }
    }
    
    setupAudio() {
        this.audioInitialized = false;
        this.sounds = {};
    }
    
    initAudio() {
        if (this.audioInitialized) return;
        
        try {
            this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
            this.sounds = {
                shoot: this.createShootSound.bind(this),
                hit: this.createHitSound.bind(this),
                pickup: this.createPickupSound.bind(this),
                door: this.createDoorSound.bind(this)
            };
            this.audioInitialized = true;
            console.log('Audio initialized!');
            
            // Start background music
            this.startMusic();
        } catch(e) {
            console.log('Web Audio not supported:', e);
            this.audioContext = null;
        }
    }
    
    startMusic() {
        if (!this.audioContext || this.musicPlaying) return;
        
        // Create a simple looping bassline
        const bass = this.audioContext.createOscillator();
        this.musicGain = this.audioContext.createGain();
        
        bass.type = 'triangle';
        bass.frequency.setValueAtTime(55, this.audioContext.currentTime); // A1
        this.musicGain.gain.setValueAtTime(0.08 * this.masterVolume, this.audioContext.currentTime);
        
        bass.connect(this.musicGain);
        this.musicGain.connect(this.audioContext.destination);
        
        // Bassline pattern (repeating every 4 seconds)
        const now = this.audioContext.currentTime;
        const notes = [55, 55, 82.5, 55, 73.5, 55, 82.5, 65.5]; // A, A, E, A, D, A, E, C
        const beatLength = 0.5;
        
        const playBassPattern = () => {
            const startTime = this.audioContext.currentTime;
            notes.forEach((freq, i) => {
                bass.frequency.setValueAtTime(freq, startTime + i * beatLength);
            });
            
            // Loop every 4 seconds
            setTimeout(playBassPattern, 4000);
        };
        
        bass.start();
        playBassPattern();
        
        this.musicPlaying = true;
        this.musicOscillator = bass;
        
        console.log('Music started at volume:', this.masterVolume);
    }
    
    stopMusic() {
        if (this.musicOscillator) {
            this.musicOscillator.stop();
            this.musicPlaying = false;
            this.musicOscillator = null;
        }
    }
    
    playSound(soundName) {
        if (!this.audioInitialized) {
            this.initAudio();
        }
        
        if (!this.audioContext || !this.sounds[soundName]) return;
        
        try {
            this.sounds[soundName]();
        } catch(e) {
            console.log('Error playing sound:', e);
        }
    }
    
    createShootSound() {
        const ctx = this.audioContext;
        const oscillator = ctx.createOscillator();
        const gainNode = ctx.createGain();
        
        oscillator.connect(gainNode);
        gainNode.connect(ctx.destination);
        
        oscillator.frequency.setValueAtTime(200, ctx.currentTime);
        oscillator.frequency.exponentialRampToValueAtTime(50, ctx.currentTime + 0.1);
        
        gainNode.gain.setValueAtTime(0.3 * this.masterVolume, ctx.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, ctx.currentTime + 0.1);
        
        oscillator.start(ctx.currentTime);
        oscillator.stop(ctx.currentTime + 0.1);
    }
    
    createHitSound() {
        const ctx = this.audioContext;
        const oscillator = ctx.createOscillator();
        const gainNode = ctx.createGain();
        
        oscillator.connect(gainNode);
        gainNode.connect(ctx.destination);
        
        oscillator.frequency.setValueAtTime(150, ctx.currentTime);
        oscillator.frequency.exponentialRampToValueAtTime(20, ctx.currentTime + 0.2);
        
        gainNode.gain.setValueAtTime(0.4 * this.masterVolume, ctx.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, ctx.currentTime + 0.2);
        
        oscillator.start(ctx.currentTime);
        oscillator.stop(ctx.currentTime + 0.2);
    }
    
    createPickupSound() {
        const ctx = this.audioContext;
        const oscillator = ctx.createOscillator();
        const gainNode = ctx.createGain();
        
        oscillator.connect(gainNode);
        gainNode.connect(ctx.destination);
        
        oscillator.frequency.setValueAtTime(400, ctx.currentTime);
        oscillator.frequency.exponentialRampToValueAtTime(800, ctx.currentTime + 0.1);
        
        gainNode.gain.setValueAtTime(0.2 * this.masterVolume, ctx.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, ctx.currentTime + 0.1);
        
        oscillator.start(ctx.currentTime);
        oscillator.stop(ctx.currentTime + 0.1);
    }
    
    createDoorSound() {
        const ctx = this.audioContext;
        const oscillator = ctx.createOscillator();
        const gainNode = ctx.createGain();
        
        oscillator.connect(gainNode);
        gainNode.connect(ctx.destination);
        
        oscillator.frequency.setValueAtTime(100, ctx.currentTime);
        oscillator.frequency.linearRampToValueAtTime(150, ctx.currentTime + 0.3);
        
        gainNode.gain.setValueAtTime(0.2 * this.masterVolume, ctx.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, ctx.currentTime + 0.3);
        
        oscillator.start(ctx.currentTime);
        oscillator.stop(ctx.currentTime + 0.3);
    }
    
    setupControls() {
        document.addEventListener('keydown', (e) => {
            // Check for exit FIRST (before engine consumes spacebar)
            if ((e.key === ' ' || e.key === 'Spacebar') && this.running) {
                const dx = this.exitX + 0.5 - this.engine.player.x;
                const dy = this.exitY + 0.5 - this.engine.player.y;
                const dist = Math.sqrt(dx * dx + dy * dy);
                
                console.log('Spacebar pressed! Distance to exit:', dist);
                
                if (dist < 2) {
                    console.log('Close enough to exit - calling levelComplete()');
                    this.levelComplete();
                    return; // Don't let engine handle this
                }
            }
            
            // Now let engine handle the key
            this.engine.handleKeyDown(e);
            
            // Weapon switching
            const key = e.key;
            if (key >= '1' && key <= '6') {
                const weaponIndex = parseInt(key) - 1;
                const weaponName = this.weaponOrder[weaponIndex];
                if (this.unlockedWeapons.has(weaponName)) {
                    this.currentWeaponIndex = weaponIndex;
                    this.currentWeapon = this.weapons[weaponName];
                }
            }
            
            // Mouse wheel alternative
            if (e.key === 'q') {
                this.previousWeapon();
            }
            if (e.key === 'e') {
                this.nextWeapon();
            }
        });
        
        document.addEventListener('keyup', (e) => this.engine.handleKeyUp(e));
        document.addEventListener('mousemove', (e) => this.engine.handleMouseMove(e));
        
        this.canvas.addEventListener('click', (e) => {
            this.engine.handleClick();
            if (this.running && this.engine.mouseLocked) {
                this.shoot();
            }
        });
        
        this.canvas.addEventListener('wheel', (e) => {
            if (this.running && this.engine.mouseLocked) {
                e.preventDefault();
                if (e.deltaY > 0) {
                    this.nextWeapon();
                } else {
                    this.previousWeapon();
                }
            }
        });
        
        document.addEventListener('pointerlockchange', () => {
            this.engine.handlePointerLockChange();
        });
        
        document.getElementById('startButton').addEventListener('click', () => this.startGame());
        document.getElementById('continueButton').addEventListener('click', () => this.continueGame());
        document.getElementById('newGameButton').addEventListener('click', () => this.newGame());
        document.getElementById('restartButton').addEventListener('click', () => this.restart());
        document.getElementById('nextLevelButton').addEventListener('click', () => this.nextLevel());
        document.getElementById('saveButton').addEventListener('click', () => this.saveGame());
        
        // Volume controls
        const volumeSlider = document.getElementById('volumeSlider');
        const volumePercent = document.getElementById('volumePercent');
        const testSoundButton = document.getElementById('testSoundButton');
        
        if (volumeSlider) {
            // Set initial value from saved volume
            volumeSlider.value = this.masterVolume * 100;
            volumePercent.textContent = Math.round(this.masterVolume * 100);
            
            volumeSlider.addEventListener('input', (e) => {
                const volume = parseInt(e.target.value) / 100;
                this.setVolume(volume);
                volumePercent.textContent = Math.round(volume * 100);
            });
        }
        
        if (testSoundButton) {
            testSoundButton.addEventListener('click', () => {
                // Initialize audio if needed
                if (!this.audioInitialized) {
                    this.initAudio();
                }
                // Play test sound
                this.playSound('shoot');
            });
        }
        
        // Add quit button handler if it exists
        const quitButton = document.getElementById('quitButton');
        if (quitButton) {
            quitButton.addEventListener('click', () => this.quitToMenu());
        }
    }
    
    quitToMenu() {
        // Release pointer lock
        if (document.pointerLockElement) {
            document.exitPointerLock();
        }
        
        // Hide all overlays
        this.ui.gameOver.classList.add('hidden');
        this.ui.levelComplete.style.display = 'none';
        this.ui.container.classList.add('hidden');
        
        // Hide weapon indicator
        if (this.ui.weaponIndicator) {
            this.ui.weaponIndicator.classList.add('hidden');
        }
        
        // Show main menu
        this.ui.menu.classList.remove('hidden');
        
        this.running = false;
    }
    
    previousWeapon() {
        let index = this.currentWeaponIndex;
        do {
            index = (index - 1 + this.weaponOrder.length) % this.weaponOrder.length;
        } while (!this.unlockedWeapons.has(this.weaponOrder[index]) && index !== this.currentWeaponIndex);
        
        this.currentWeaponIndex = index;
        this.currentWeapon = this.weapons[this.weaponOrder[index]];
    }
    
    nextWeapon() {
        let index = this.currentWeaponIndex;
        do {
            index = (index + 1) % this.weaponOrder.length;
        } while (!this.unlockedWeapons.has(this.weaponOrder[index]) && index !== this.currentWeaponIndex);
        
        this.currentWeaponIndex = index;
        this.currentWeapon = this.weapons[this.weaponOrder[index]];
    }
    
    generateLevel() {
        const levelData = this.levelGen.generate(this.level);
        
        this.engine.setMap(levelData.map);
        this.engine.setDoors(levelData.doors);
        this.engine.player.x = levelData.spawn.x;
        this.engine.player.y = levelData.spawn.y;
        
        this.exitX = levelData.exit.x;
        this.exitY = levelData.exit.y;
        
        // Create enemies
        this.enemies = levelData.enemies.map(e => new Enemy(e.x, e.y, e.type));
        
        // Create pickups
        this.pickups = levelData.pickups.map(p => new Pickup(p.x, p.y, p.type));
        
        // Create keys
        this.keyItems = levelData.keys.map(k => new Key(k.x, k.y, k.color));
        
        // Unlock weapons based on level
        if (this.level >= 2 && !this.unlockedWeapons.has('shotgun')) {
            this.unlockedWeapons.add('shotgun');
            this.weapons.shotgun.ammo = 50;
        }
        if (this.level >= 4 && !this.unlockedWeapons.has('chaingun')) {
            this.unlockedWeapons.add('chaingun');
            this.weapons.chaingun.ammo = 200;
        }
        if (this.level >= 6 && !this.unlockedWeapons.has('rocket')) {
            this.unlockedWeapons.add('rocket');
            this.weapons.rocket.ammo = 30;
        }
        if (this.level >= 8 && !this.unlockedWeapons.has('plasma')) {
            this.unlockedWeapons.add('plasma');
            this.weapons.plasma.ammo = 200;
        }
        if (this.level >= 10 && !this.unlockedWeapons.has('bfg')) {
            this.unlockedWeapons.add('bfg');
            this.weapons.bfg.ammo = 50;
        }
        
        this.kills = 0;
    }
    
    startGame() {
        console.log('=== STARTING GAME ===');
        console.log('Level:', this.level);
        console.log('Pistol ammo:', this.weapons.pistol.ammo, 'Infinite:', this.weapons.pistol.infinite);
        
        this.ui.menu.classList.add('hidden');
        this.ui.container.classList.remove('hidden');
        
        // Show weapon indicator
        if (this.ui.weaponIndicator) {
            this.ui.weaponIndicator.classList.remove('hidden');
        }
        
        // Show canvas
        this.canvas.style.display = 'block';
        
        this.generateLevel();
        this.running = true;
        this.updateUI();
        
        console.log('Enemies spawned:', this.enemies.length);
        console.log('Pickups spawned:', this.pickups.length);
        console.log('Keys spawned:', this.keyItems.length);
        console.log('Sprites loaded:', Object.keys(SPRITES).length);
        
        this.gameLoop();
    }
    
    newGame() {
        this.level = 1;
        this.totalKills = 0;
        this.health = this.maxHealth;
        this.armor = 0;
        this.keys = { red: false, blue: false, yellow: false };
        this.unlockedWeapons = new Set(['pistol']);
        this.currentWeaponIndex = 0;
        this.currentWeapon = this.weapons.pistol;
        
        // Reset ammo
        Object.values(this.weapons).forEach(w => {
            if (w.infinite) {
                w.ammo = 999;
            } else {
                w.ammo = 0;
            }
        });
        
        // Hide game over screen
        this.ui.gameOver.classList.add('hidden');
        
        this.startGame();
    }
    
    continueGame() {
        this.startGame();
    }
    
    nextLevel() {
        this.level++;
        this.ui.levelComplete.classList.add('hidden');
        
        // Show canvas again
        this.canvas.style.display = 'block';
        
        // Make sure music is still playing
        if (this.audioInitialized && !this.musicPlaying) {
            this.startMusic();
        }
        
        this.generateLevel();
        this.running = true;
        this.updateUI();
        this.saveGame();
        
        // Re-lock pointer after a brief delay
        setTimeout(() => {
            if (this.canvas && this.running) {
                this.canvas.requestPointerLock();
            }
        }, 100);
    }
    
    restart() {
        this.health = this.maxHealth;
        this.armor = 50; // Give some armor on restart
        
        // Hide game over screen
        this.ui.gameOver.classList.add('hidden');
        
        // Show canvas
        this.canvas.style.display = 'block';
        
        this.generateLevel();
        this.running = true;
        this.updateUI();
    }
    
    saveGame() {
        const saveData = {
            level: this.level,
            health: this.health,
            armor: this.armor,
            totalKills: this.totalKills,
            keys: this.keys,
            unlockedWeapons: Array.from(this.unlockedWeapons),
            weaponAmmo: Object.entries(this.weapons).reduce((acc, [name, weapon]) => {
                acc[name] = weapon.ammo;
                return acc;
            }, {})
        };
        
        localStorage.setItem('crimsonDoomSave', JSON.stringify(saveData));
        console.log('Game saved!');
    }
    
    loadGame() {
        const saved = localStorage.getItem('crimsonDoomSave');
        if (saved) {
            const data = JSON.parse(saved);
            this.level = data.level || 1;
            this.health = data.health || 100;
            this.armor = data.armor || 0;
            this.totalKills = data.totalKills || 0;
            this.keys = data.keys || { red: false, blue: false, yellow: false };
            
            if (data.unlockedWeapons) {
                this.unlockedWeapons = new Set(data.unlockedWeapons);
            }
            
            if (data.weaponAmmo) {
                Object.entries(data.weaponAmmo).forEach(([name, ammo]) => {
                    if (this.weapons[name]) {
                        this.weapons[name].ammo = ammo;
                    }
                });
            }
            
            document.getElementById('continueButton').style.display = 'block';
            console.log('Save game loaded!');
        }
    }
    
    shoot() {
        if (!this.currentWeapon.fire()) return;
        
        this.playSound('shoot');
        this.muzzleFlash = 10;
        this.screenShake = this.currentWeapon.name === 'BFG' ? 10 : 5;
        
        for (let i = 0; i < this.currentWeapon.pellets; i++) {
            const spread = (Math.random() - 0.5) * this.currentWeapon.spread;
            const hitEnemy = this.checkRayHit(this.engine.player.angle + spread);
            
            if (hitEnemy && hitEnemy.takeDamage(this.currentWeapon.damage)) {
                this.kills++;
                this.totalKills++;
                this.playSound('hit');
            }
        }
        
        this.updateUI();
    }
    
    checkRayHit(angle) {
        const playerX = this.engine.player.x;
        const playerY = this.engine.player.y;
        
        let closestEnemy = null;
        let closestDist = Infinity;
        
        this.enemies.forEach(enemy => {
            if (enemy.dead) return;
            
            // Vector from player to enemy
            const dx = enemy.x - playerX;
            const dy = enemy.y - playerY;
            const distance = Math.sqrt(dx * dx + dy * dy);
            
            // Normalize enemy direction
            const enemyAngle = Math.atan2(dy, dx);
            
            // Calculate angle difference
            let angleDiff = enemyAngle - angle;
            // Normalize to -PI to PI
            while (angleDiff > Math.PI) angleDiff -= 2 * Math.PI;
            while (angleDiff < -Math.PI) angleDiff += 2 * Math.PI;
            
            // Calculate perpendicular distance from ray to enemy center
            const perpDist = Math.abs(distance * Math.sin(angleDiff));
            
            // Enemy hitbox radius
            const hitRadius = 0.3; // Made slightly smaller
            
            // Check if ray passes through enemy hitbox
            if (perpDist < hitRadius && distance < 20 && Math.abs(angleDiff) < Math.PI/3) {
                // WALL CHECK - Cast ray to enemy and check for walls
                const steps = Math.floor(distance * 2);
                const stepX = dx / steps;
                const stepY = dy / steps;
                let blocked = false;
                
                for (let i = 1; i < steps; i++) {
                    const checkX = playerX + stepX * i;
                    const checkY = playerY + stepY * i;
                    
                    if (!this.engine.canMove(checkX, checkY)) {
                        blocked = true;
                        break;
                    }
                }
                
                if (!blocked && distance < closestDist) {
                    closestDist = distance;
                    closestEnemy = enemy;
                }
            }
        });
        
        return closestEnemy;
    }
    
    update() {
        if (!this.running) return; // Don't update if game stopped
        
        this.time++;
        this.engine.update();
        this.updateEnemies();
        this.updatePickups();
        this.updateKeys();
        this.currentWeapon.update();
    }
    
    updateEnemies() {
        this.enemies.forEach(enemy => {
            const shouldAttack = enemy.update(
                this.engine.player.x, 
                this.engine.player.y,
                (x, y) => this.engine.canMove(x, y)
            );
            
            if (shouldAttack) {
                const damage = enemy.attack();
                this.takeDamage(damage);
            }
        });
    }
    
    updatePickups() {
        this.pickups.forEach(pickup => {
            pickup.update(this.time);
            
            if (!pickup.collected && pickup.checkCollision(this.engine.player.x, this.engine.player.y)) {
                const result = pickup.collect(this);
                if (result) {
                    this.playSound('pickup');
                    // Show pickup message
                    if (pickup.type === 'health') {
                        this.showPickupMessage('+25 HEALTH');
                    } else if (pickup.type === 'armor') {
                        this.showPickupMessage('+25 ARMOR');
                    } else if (pickup.type === 'ammo') {
                        this.showPickupMessage('+50 AMMO');
                    }
                    this.updateUI();
                }
            }
        });
        
        // Update pickup timer
        if (this.pickupTimer > 0) {
            this.pickupTimer--;
        }
    }
    
    showPickupMessage(message) {
        this.pickupMessage = message;
        this.pickupTimer = 60; // 1 second at 60fps
    }
    
    updateKeys() {
        this.keyItems.forEach(key => {
            key.update(this.time);
            
            if (!key.collected && key.checkCollision(this.engine.player.x, this.engine.player.y)) {
                key.collected = true;
                this.keys[key.color] = true;
                this.engine.unlockDoor(key.color);
                this.playSound('door');
                this.updateUI();
                console.log('Picked up', key.color, 'key!');
            }
        });
    }
    
    takeDamage(amount) {
        const armorDamage = Math.min(this.armor, amount * 0.5);
        this.armor -= armorDamage;
        
        const healthDamage = amount - armorDamage;
        this.health -= healthDamage;
        
        this.damageFlash = 20;
        this.screenShake = 8;
        
        if (this.health <= 0) {
            this.gameOver();
        }
        
        this.updateUI();
    }
    
    gameOver() {
        this.running = false;
        
        // Release pointer lock
        if (document.pointerLockElement) {
            document.exitPointerLock();
        }
        
        // Hide canvas
        this.canvas.style.display = 'none';
        
        this.ui.finalKills.textContent = this.totalKills;
        this.ui.finalLevel.textContent = this.level;
        this.ui.gameOver.classList.remove('hidden');
    }
    
    levelComplete() {
        this.running = false;
        
        console.log('=== LEVEL COMPLETE ===');
        console.log('Running:', this.running);
        
        // Release pointer lock
        if (document.pointerLockElement) {
            document.exitPointerLock();
            console.log('Pointer unlocked');
        }
        
        // Hide canvas to ensure overlay shows
        console.log('Canvas element:', this.canvas);
        console.log('Canvas display before:', this.canvas.style.display);
        this.canvas.style.display = 'none';
        console.log('Canvas display after:', this.canvas.style.display);
        
        // Show level complete screen
        this.ui.levelComplete.classList.remove('hidden');
        console.log('Level complete screen shown');
        console.log('Level complete classes:', this.ui.levelComplete.className);
        
        document.getElementById('levelNumber').textContent = this.level;
        document.getElementById('levelKills').textContent = this.kills;
    }
    
    updateUI() {
        this.ui.health.textContent = Math.max(0, Math.floor(this.health));
        this.ui.armor.textContent = Math.floor(this.armor);
        this.ui.ammo.textContent = this.currentWeapon.infinite ? '' : this.currentWeapon.ammo;
        this.ui.weapon.textContent = this.currentWeapon.name;
        this.ui.kills.textContent = this.kills;
        this.ui.level.textContent = this.level;
        
        // Update weapon indicator
        if (this.ui.currentWeaponName) {
            this.ui.currentWeaponName.textContent = this.currentWeapon.name;
        }
        
        // Update key display
        const keyText = [];
        if (this.keys.red) keyText.push('');
        if (this.keys.blue) keyText.push('');
        if (this.keys.yellow) keyText.push('');
        this.ui.keys.textContent = keyText.join(' ');
    }
    
    render() {
        if (!this.running) return; // Don't render if game stopped
        
        this.engine.render();
        this.renderSprites();
        this.renderEffects();
    }
    
    renderSprites() {
        const ctx = this.engine.ctx;
        
        // DEBUG: Draw enemy hitboxes (comment out to disable)
        const DEBUG_HITBOXES = false;
        
        if (DEBUG_HITBOXES) {
            this.enemies.forEach(enemy => {
                if (enemy.dead) return;
                
                const dx = enemy.x - this.engine.player.x;
                const dy = enemy.y - this.engine.player.y;
                const dist = Math.sqrt(dx * dx + dy * dy);
                const angle = Math.atan2(dy, dx);
                
                let relAngle = angle - this.engine.player.angle;
                while (relAngle > Math.PI) relAngle -= 2 * Math.PI;
                while (relAngle < -Math.PI) relAngle += 2 * Math.PI;
                
                if (Math.abs(relAngle) < this.engine.player.fov / 2 && dist < 15) {
                    const screenX = (relAngle / this.engine.player.fov + 0.5) * this.canvas.width;
                    const size = (this.canvas.height / dist) * 0.4; // Hitbox radius visualization
                    
                    ctx.strokeStyle = 'red';
                    ctx.lineWidth = 2;
                    ctx.strokeRect(
                        screenX - size/2,
                        this.canvas.height/2 - size/2,
                        size,
                        size
                    );
                }
            });
        }
        
        // Render EXIT PORTAL (most important - render first so it's behind everything)
        const exitDx = this.exitX + 0.5 - this.engine.player.x;
        const exitDy = this.exitY + 0.5 - this.engine.player.y;
        const exitDist = Math.sqrt(exitDx * exitDx + exitDy * exitDy);
        
        // Only render exit if within reasonable distance
        if (exitDist < 10) {
            // Create a pulsing portal effect
            const pulseScale = 1.0 + Math.sin(this.time * 0.05) * 0.2;
            
            // Draw a large glowing portal
            const exitAngle = Math.atan2(exitDy, exitDx);
            let exitRelAngle = exitAngle - this.engine.player.angle;
            while (exitRelAngle > Math.PI) exitRelAngle -= 2 * Math.PI;
            while (exitRelAngle < -Math.PI) exitRelAngle += 2 * Math.PI;
            
            if (Math.abs(exitRelAngle) < this.engine.player.fov / 2) {
                const exitScreenX = (exitRelAngle / this.engine.player.fov + 0.5) * this.canvas.width;
                const exitSize = Math.min((this.canvas.height / exitDist) * 1.2 * pulseScale, this.canvas.height * 0.5);
                const exitScreenY = (this.canvas.height / 2) - (exitSize / 2);
                
                // Draw glowing portal
                ctx.save();
                ctx.globalAlpha = 0.7;
                
                // Outer glow
                const gradient = ctx.createRadialGradient(
                    exitScreenX, exitScreenY + exitSize/2, 0,
                    exitScreenX, exitScreenY + exitSize/2, exitSize
                );
                gradient.addColorStop(0, 'rgba(0, 255, 255, 0.8)');
                gradient.addColorStop(0.5, 'rgba(0, 200, 255, 0.4)');
                gradient.addColorStop(1, 'rgba(0, 100, 200, 0)');
                
                ctx.fillStyle = gradient;
                ctx.fillRect(
                    exitScreenX - exitSize,
                    exitScreenY - exitSize/2,
                    exitSize * 2,
                    exitSize * 2
                );
                
                // Portal text
                ctx.globalAlpha = 1.0;
                ctx.fillStyle = '#00FFFF';
                ctx.font = `${Math.max(12, Math.min(exitSize * 0.2, 32))}px "Courier New"`;
                ctx.textAlign = 'center';
                ctx.fillText('EXIT', exitScreenX, exitScreenY + exitSize/2);
                
                // Distance indicator
                if (exitDist < 2) {
                    ctx.fillStyle = '#FFFF00';
                    ctx.font = `${Math.max(10, Math.min(exitSize * 0.15, 20))}px "Courier New"`;
                    ctx.fillText('PRESS SPACE', exitScreenX, exitScreenY + exitSize/2 + 20);
                }
                
                ctx.restore();
            }
        }
        
        // Render pickups
        this.pickups.forEach(pickup => {
            if (!pickup.collected) {
                this.renderWorldSprite(pickup.x, pickup.y + pickup.bob, SPRITES[pickup.type === 'health' ? 'medikit' : pickup.type === 'armor' ? 'armor_bonus' : 'ammo_clip'], 0.3);
            }
        });
        
        // Render keys
        this.keyItems.forEach(key => {
            if (!key.collected) {
                this.renderWorldSprite(key.x, key.y + key.bob, SPRITES['key_' + key.color], 0.4);
            }
        });
        
        // Render enemies
        const sortedEnemies = this.enemies
            .map(e => {
                const dx = e.x - this.engine.player.x;
                const dy = e.y - this.engine.player.y;
                return {
                    enemy: e,
                    distance: Math.sqrt(dx * dx + dy * dy),
                    angle: Math.atan2(dy, dx)
                };
            })
            .sort((a, b) => b.distance - a.distance);
        
        sortedEnemies.forEach(({enemy, distance, angle}) => {
            const sprite = enemy.dead ? SPRITES.enemy_dead : SPRITES.enemy;
            this.renderWorldSprite(enemy.x, enemy.y, sprite, 0.8, distance, angle, enemy);
        });
    }
    
    renderWorldSprite(x, y, sprite, scale, distance = null, angle = null, enemy = null) {
        const ctx = this.engine.ctx;
        
        if (distance === null) {
            const dx = x - this.engine.player.x;
            const dy = y - this.engine.player.y;
            distance = Math.sqrt(dx * dx + dy * dy);
            angle = Math.atan2(dy, dx);
        }
        
        let relativeAngle = angle - this.engine.player.angle;
        while (relativeAngle > Math.PI) relativeAngle -= 2 * Math.PI;
        while (relativeAngle < -Math.PI) relativeAngle += 2 * Math.PI;
        
        if (Math.abs(relativeAngle) > this.engine.player.fov / 2) return;
        
        const screenX = (relativeAngle / this.engine.player.fov + 0.5) * this.canvas.width;
        const spriteHeight = (this.canvas.height / distance) * scale;
        const spriteWidth = spriteHeight * (sprite.width / sprite.height);
        const screenY = (this.canvas.height / 2) - (spriteHeight / 2);
        
        const shade = Math.max(0.3, 1 - (distance / this.engine.maxDepth));
        ctx.globalAlpha = shade;
        
        ctx.drawImage(
            sprite,
            screenX - spriteWidth / 2,
            screenY,
            spriteWidth,
            spriteHeight
        );
        
        ctx.globalAlpha = 1.0;
        
        // Health bar for enemies
        if (enemy && !enemy.dead) {
            const barWidth = spriteWidth;
            const barHeight = 4;
            ctx.fillStyle = '#000';
            ctx.fillRect(screenX - barWidth / 2, screenY - 10, barWidth, barHeight);
            ctx.fillStyle = enemy.health > 30 ? '#0f0' : '#f00';
            ctx.fillRect(screenX - barWidth / 2, screenY - 10, barWidth * (enemy.health / enemy.maxHealth), barHeight);
        }
    }
    
    renderEffects() {
        const ctx = this.engine.ctx;
        
        // Muzzle flash
        if (this.muzzleFlash > 0) {
            ctx.fillStyle = `rgba(255, 200, 0, ${this.muzzleFlash / 10})`;
            ctx.fillRect(this.canvas.width / 2 - 30, this.canvas.height - 100, 60, 60);
            this.muzzleFlash--;
        }
        
        // Damage flash
        if (this.damageFlash > 0) {
            ctx.fillStyle = `rgba(255, 0, 0, ${this.damageFlash / 40})`;
            ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
            this.damageFlash--;
        }
        
        // Crosshair
        ctx.strokeStyle = '#fff';
        ctx.lineWidth = 2;
        const centerX = this.canvas.width / 2;
        const centerY = this.canvas.height / 2;
        ctx.beginPath();
        ctx.moveTo(centerX - 10, centerY);
        ctx.lineTo(centerX + 10, centerY);
        ctx.moveTo(centerX, centerY - 10);
        ctx.lineTo(centerX, centerY + 10);
        ctx.stroke();
        
        // Pickup message
        if (this.pickupTimer > 0) {
            const alpha = Math.min(1, this.pickupTimer / 15);
            ctx.fillStyle = `rgba(0, 255, 0, ${alpha})`;
            ctx.font = '20px "Courier New"';
            ctx.textAlign = 'center';
            ctx.fillText(this.pickupMessage, centerX, centerY - 60);
        }
        
        // Render minimap
        this.renderMinimap();
        
        this.renderWeapon();
    }
    
    renderMinimap() {
        const ctx = this.engine.ctx;
        const map = this.engine.map;
        if (!map) return;
        
        const minimapSize = 100;
        const minimapX = 10;
        const minimapY = 10;
        const cellSize = minimapSize / Math.max(map.length, map[0].length);
        const viewRadius = 8; // How many cells around player to show
        
        // Draw minimap background
        ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
        ctx.fillRect(minimapX - 2, minimapY - 2, minimapSize + 4, minimapSize + 4);
        
        // Draw visible area of the map centered on player
        const playerMapX = Math.floor(this.engine.player.x);
        const playerMapY = Math.floor(this.engine.player.y);
        
        for (let dy = -viewRadius; dy <= viewRadius; dy++) {
            for (let dx = -viewRadius; dx <= viewRadius; dx++) {
                const mapX = playerMapX + dx;
                const mapY = playerMapY + dy;
                
                if (mapY >= 0 && mapY < map.length && mapX >= 0 && mapX < map[0].length) {
                    const cell = map[mapY][mapX];
                    const screenX = minimapX + (dx + viewRadius) * (minimapSize / (viewRadius * 2 + 1));
                    const screenY = minimapY + (dy + viewRadius) * (minimapSize / (viewRadius * 2 + 1));
                    const cellDrawSize = minimapSize / (viewRadius * 2 + 1);
                    
                    // Wall colors
                    if (cell === 0) {
                        ctx.fillStyle = '#333'; // Floor
                    } else if (cell === 9) {
                        ctx.fillStyle = '#0ff'; // Exit
                    } else if (cell >= 5 && cell <= 7) {
                        // Doors
                        ctx.fillStyle = cell === 5 ? '#a00' : cell === 6 ? '#00a' : '#aa0';
                    } else {
                        ctx.fillStyle = '#666'; // Walls
                    }
                    
                    ctx.fillRect(screenX, screenY, cellDrawSize, cellDrawSize);
                }
            }
        }
        
        // Draw enemies on minimap
        this.enemies.forEach(enemy => {
            if (enemy.dead) return;
            const dx = enemy.x - this.engine.player.x;
            const dy = enemy.y - this.engine.player.y;
            if (Math.abs(dx) <= viewRadius && Math.abs(dy) <= viewRadius) {
                const screenX = minimapX + (dx + viewRadius) * (minimapSize / (viewRadius * 2 + 1));
                const screenY = minimapY + (dy + viewRadius) * (minimapSize / (viewRadius * 2 + 1));
                ctx.fillStyle = '#f00';
                ctx.beginPath();
                ctx.arc(screenX, screenY, 2, 0, Math.PI * 2);
                ctx.fill();
            }
        });
        
        // Draw pickups on minimap
        this.pickups.forEach(pickup => {
            if (pickup.collected) return;
            const dx = pickup.x - this.engine.player.x;
            const dy = pickup.y - this.engine.player.y;
            if (Math.abs(dx) <= viewRadius && Math.abs(dy) <= viewRadius) {
                const screenX = minimapX + (dx + viewRadius) * (minimapSize / (viewRadius * 2 + 1));
                const screenY = minimapY + (dy + viewRadius) * (minimapSize / (viewRadius * 2 + 1));
                ctx.fillStyle = pickup.type === 'health' ? '#0f0' : pickup.type === 'armor' ? '#00f' : '#ff0';
                ctx.beginPath();
                ctx.arc(screenX, screenY, 2, 0, Math.PI * 2);
                ctx.fill();
            }
        });
        
        // Draw player on minimap (center)
        const playerScreenX = minimapX + minimapSize / 2;
        const playerScreenY = minimapY + minimapSize / 2;
        
        ctx.fillStyle = '#0f0';
        ctx.beginPath();
        ctx.arc(playerScreenX, playerScreenY, 3, 0, Math.PI * 2);
        ctx.fill();
        
        // Draw player direction
        const dirLen = 8;
        ctx.strokeStyle = '#0f0';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(playerScreenX, playerScreenY);
        ctx.lineTo(
            playerScreenX + Math.cos(this.engine.player.angle) * dirLen,
            playerScreenY + Math.sin(this.engine.player.angle) * dirLen
        );
        ctx.stroke();
    }
    
    renderWeapon() {
        const ctx = this.engine.ctx;
        const weaponY = this.canvas.height - 180 + (this.screenShake > 0 ? Math.random() * this.screenShake : 0);
        
        const weaponSprite = this.muzzleFlash > 5 ? 
            SPRITES[this.currentWeapon.fireSpriteName] :
            SPRITES[this.currentWeapon.spriteName];
        
        if (!weaponSprite) return;
        
        const scale = 2.5;
        const weaponWidth = weaponSprite.width * scale;
        const weaponHeight = weaponSprite.height * scale;
        
        ctx.drawImage(
            weaponSprite,
            this.canvas.width / 2 - weaponWidth / 2,
            weaponY,
            weaponWidth,
            weaponHeight
        );
        
        if (this.screenShake > 0) this.screenShake--;
    }
    
    gameLoop() {
        if (!this.running) return;
        
        this.update();
        this.render();
        
        requestAnimationFrame(() => this.gameLoop());
    }
}

window.addEventListener('load', () => {
    new CrimsonDoom();
});
